        // pmull2  v11.1q, v11.2d, v14.2d                               // ................................................................*...
        // eor     v10.16b, v10.16b, v11.16b                            // ..................................................................*.
        // ldp x22, x23, [x0, #(5*16)]                                  // .....................*..............................................
        // add        w14, w13, #5                                      // .....*..............................................................
        // rev        w14, w14                                          // ......*.............................................................
        // orr        x14, x12, x14, lsl #32                            // .......*............................................................
        // stp        x11, x14, [sp, #(STACK_BASE_AES_ST + 5*16)]       // .....................*..............................................
        // ldr        q0, [sp, #(STACK_BASE_AES_ST + 5*16)]             // ......................*.............................................
        // aesr v0.16b, v18.16b                                         // ..............................*.....................................
        // aesr v0.16b, v19.16b                                         // ...............................*....................................
        // aesr v0.16b, v20.16b                                         // .................................*..................................
        // aesr v0.16b, v21.16b                                         // .................................*..................................
        // aesr v0.16b, v22.16b                                         // ...................................*................................
        // aesr v0.16b, v23.16b                                         // .............................................*......................
        // aesr v0.16b, v24.16b                                         // ..............................................*.....................
        // aesr v0.16b, v25.16b                                         // ...................................................*................
        // aesr v0.16b, v26.16b                                         // ....................................................*...............
        // aese v0.16b, v27.16b                                         // ......................................................*.............
        // eor x22, x22, x20                                            // ........................*...........................................
        // eor x23, x23, x21                                            // ........................*...........................................
        // stp x22, x23, [sp, #(STACK_BASE_AES_ST + 5*16)]              // .........................*..........................................
        // ldr q29, [sp, #(STACK_BASE_AES_ST + 5*16)]                   // .....................................................*..............
        // eor v0.16b, v29.16b, v0.16b                                  // .......................................................*............
        // str q0, [x2, #(5*16)]                                        // .........................................................*..........
        // ldr q15, [x6, #48]                                           // .............................................................*......
        // ldr q16, [x6, #80]                                           // _..................................................................*
        // ldr q17, [x6, #64]                                           // .....................................................*..............
        // rev64   v0.16b, v0.16b                                       // .........................................................*..........
        // pmull   v11.1q, v0.1d, v15.1d                                // ................................................................*...
        // eor     v8.16b, v8.16b, v11.16b                              // .................................................................*..
        // pmull2  v11.1q, v0.2d, v15.2d                                // .................................................................*..
        // eor     v9.16b, v9.16b, v11.16b                              // ..................................................................*.
        // mov     d11, v0.d[1]                                         // ..........................................................*.........
        // eor     v11.8b, v11.8b, v0.8b                                // ............................................................*.......
        // pmull   v11.1q, v11.1d, v17.1d                               // ..................................................................*.
        // eor     v10.16b, v10.16b, v11.16b                            // _..................................................................*

        sub count, count, #1
        cbnz count, Lloop_unrolled_start
Lloop_unrolled_core_end:

        ldp plain_lo, plain_hi, [input, #(4*16)]
        aes_full_block aes_st, plain, res, 4
        str res_q, [output, #(4*16)]

        ghash_acc_1 res, Ht4, Ht34

        ldp plain_lo, plain_hi, [input, #(3*16)]
        aes_full_block aes_st, plain, res, 3
        str res_q, [output, #(3*16)]

        load_htable_56
        ghash_acc_0 res, Ht5, Ht56

        ldp plain_lo, plain_hi, [input, #(2*16)]
        aes_full_block aes_st, plain, res, 2
        str res_q, [output, #(2*16)]

        ghash_acc_1 res, Ht6, Ht56

        ldp plain_lo, plain_hi, [input, #(1*16)]
        aes_full_block aes_st, plain, res, 1
        str res_q, [output, #(1*16)]

        load_htable_78
        ghash_acc_0 res, Ht7, Ht78

        ldp plain_lo, plain_hi, [input], #(8*16)
        aes_full_block aes_st, plain, res, 0
        str res_q, [output], #(8*16)

        ghash_acc_with_tag_1 res, Ht8, Ht78, tag

        ghash_finalize tag  // @slothy:late

        add ctr, ctr, #UNROLL

Lloop_unrolled_end:

        load_htable_12
        cbz remainder, Lloop_1x_end
Lloop_1x_start:

        ldp plain_lo, plain_hi, [input], #16
        aes_full_block aes_st, plain, res, 0
        str res_q, [output], #16
        ghash_init_with_tag_0 res, Ht1, Ht12, tag

        ghash_finalize tag

        add ctr, ctr, #1
        sub remainder, remainder, #1
        cbnz remainder, Lloop_1x_start
Lloop_1x_end:

        // Return number of bytes processed
        mov x0, byte_len
        // Store new authentication tag
        rev64 tag.16b, tag.16b
        str tag_q, [tag_ptr]

        // Store updated counter
        // rev32 rtmp_ctr.16b, rtmp_ctr.16b
        // str rtmp_ctr_q, [ivec]
        rev ctr_tmp_w, ctr
        str ctr_tmp_w, [ivec, #12]

        restore_vregs
        restore_gprs

Lenc_postamble_end:
        add sp, sp, #STACK_SIZE

        ret

