        rev32 v3.16B, v29.16B                // ....*.....................................................................
        add v9.4S, v29.4S, v30.4S            // ........*.................................................................
        // gap                               // ..........................................................................
        // gap                               // ..........................................................................
        aesr v17.16b, v18.16b                // ...*......................................................................
        ldr q2, [x6, #80]                    // ........................................*.................................
        // gap                               // ..........................................................................
        // gap                               // ..........................................................................
        aesr v3.16b, v18.16b                 // .......*..................................................................
        rev32 v0.16B, v9.16B                 // ...................*......................................................
        // gap                               // ..........................................................................
        // gap                               // ..........................................................................
        eor v10.16B, v13.16B, v16.16B        // .....*....................................................................
        aesr v17.16b, v19.16b                // ......*...................................................................
        // gap                               // ..........................................................................
        // gap                               // ..........................................................................
        eor v13.16B, v5.16B, v16.16B         // .................*........................................................
        aesr v3.16b, v19.16b                 // ..........*...............................................................
        // gap                               // ..........................................................................
        // gap                               // ..........................................................................
        add v1.4S, v9.4S, v30.4S             // ..............................*...........................................
        aesr v17.16b, v20.16b                // .........*................................................................
        // gap                               // ..........................................................................
        // gap                               // ..........................................................................
        ldr q29, [x6, #48]                   // .....................................................*....................
        aesr v3.16b, v20.16b                 // ............*.............................................................
        // gap                               // ..........................................................................
        // gap                               // ..........................................................................
        aesr v17.16b, v21.16b                // ...........*..............................................................
