.align        4
_aes_gcm_enc_kernel_slothy_base_192:
aes_gcm_enc_kernel_slothy_base_192:
#ifdef BORINGSSL_DISPATCH_TEST
        adrp  x9,_BORINGSSL_function_hit@PAGE
        add   x9, x9, _BORINGSSL_function_hit@PAGEOFF
        mov   w10, #1
        strb  w10, [x9,#2] // kFlag_aes_gcm_enc_kernel
#endif

        AARCH64_SIGN_LINK_REGISTER
        sub sp, sp, #STACK_SIZE

Lenc_preamble_start:
        save_gprs
        save_vregs

        lsr byte_len, len_bits, #3

        load_round_keys
        load_tag
        load_iv

        prepare_loop_counts
        prepare_ghash

Lenc_preamble_end:

        cbz count, Lloop_unrolled_end
        ldr q1, [x6, #48]                    // .....*...............................................................................................
        rev32 v9.16B, v31.16B                // .*...................................................................................................
        // gap                               // .....................................................................................................
        // gap                               // .....................................................................................................
        add v17.4S, v31.4S, v30.4S           // ..*..................................................................................................
        ldr q4, [x6, #64]                    // *....................................................................................................
        // gap                               // .....................................................................................................
        // gap                               // .....................................................................................................
        aesr v9.16b, v18.16b                 // ...*.................................................................................................
        ldr q12, [x0, #32]                   // .........*...........................................................................................
        // gap                               // .....................................................................................................
        // gap                               // .....................................................................................................
        rev32 v6.16B, v17.16B                // ....*................................................................................................
        ldr q3, [x0, #16]                    // ............*........................................................................................
        // gap                               // .....................................................................................................
        // gap                               // .....................................................................................................
        add v17.4S, v17.4S, v30.4S           // ..............*......................................................................................
        aesr v9.16b, v19.16b                 // .......*.............................................................................................
        // gap                               // .....................................................................................................
        // gap                               // .....................................................................................................
        aesr v6.16b, v18.16b                 // ......*..............................................................................................
        ldr q29, [x6, #80]                   // .........................*...........................................................................
        // gap                               // .....................................................................................................
        // gap                               // .....................................................................................................
        aesr v9.16b, v20.16b                 // ..........*..........................................................................................
        add v14.4S, v17.4S, v30.4S           // ................*....................................................................................
        // gap                               // .....................................................................................................
        // gap                               // .....................................................................................................
        rev32 v17.16B, v17.16B               // ......................*..............................................................................
        aesr v6.16b, v19.16b                 // ........*............................................................................................
        // gap                               // .....................................................................................................
        // gap                               // .....................................................................................................
        eor v3.16B, v3.16B, v16.16B          // ...................*.................................................................................
        aesr v9.16b, v21.16b                 // .............*.......................................................................................
        // gap                               // .....................................................................................................
        // gap                               // .....................................................................................................
        aesr v6.16b, v20.16b                 // ...........*.........................................................................................
        ldr q2, [x0], #(4*16)                // .............................*.......................................................................
        // gap                               // .....................................................................................................
        // gap                               // .....................................................................................................
        eor v8.16B, v12.16B, v16.16B         // ..........................................................*..........................................
        aesr v9.16b, v22.16b                 // .................*...................................................................................
        // gap                               // .....................................................................................................
        // gap                               // .....................................................................................................
        aesr v6.16b, v21.16b                 // ...............*.....................................................................................
        ldr q31, [x6, #16]                   // ...............................*.....................................................................
        // gap                               // .....................................................................................................
        // gap                               // .....................................................................................................
