/*                            Core routine                          */
/********************************************************************/

.align        4
_aes_gcm_enc_kernel_slothy_base_256:
aes_gcm_enc_kernel_slothy_base_256:
#ifdef BORINGSSL_DISPATCH_TEST
        adrp  x9,_BORINGSSL_function_hit@PAGE
        add   x9, x9, _BORINGSSL_function_hit@PAGEOFF
        mov   w10, #1
        strb  w10, [x9,#2] // kFlag_aes_gcm_enc_kernel
#endif

        AARCH64_VALID_CALL_TARGET
        sub sp, sp, #STACK_SIZE

Lenc_preamble_start:
        save_gprs
        save_vregs

        lsr byte_len, len_bits, #3

        load_round_keys
        load_tag
        load_iv

        prepare_loop_counts
        prepare_ghash

Lenc_preamble_end:

        cbz count, Lloop_unrolled_end
        cmp count, #1
        b.eq Lloop_unrolled_start_iter_1
                                                      // Instructions:    178
                                                      // Expected cycles: 100
                                                      // Expected IPC:    1.78

                                                      // Cycle bound:     100.0
                                                      // IPC bound:       1.78

                                                      // Wall time:     22.21s
                                                      // User time:     22.21s

                                                      // ---------------------------------------- cycle (expected) ----------------------------------------->
                                                      // 0                        25                       50                       75
                                                      // |------------------------|------------------------|------------------------|
        ldr q9, [x0, #32]                             // *...................................................................................................
        ldr q5, [x6, #80]                             // *...................................................................................................
        add w24, w13, #0                              // *...................................................................................................
        add w23, w13, #2                              // *...................................................................................................
        ldr q11, [x6]                                 // .*..................................................................................................
        rev w29, w24                                  // .*..................................................................................................
        add w30, w13, #3                              // .*..................................................................................................
        rev w27, w23                                  // .*..................................................................................................
        rev w26, w30                                  // ..*.................................................................................................
        str w29, [sp, #STACK_BASE_AES_ST + 12]        // ..*.................................................................................................  // @slothy:writes=stack_0
        str w27, [sp, #STACK_BASE_AES_ST + 44]        // ..*.................................................................................................  // @slothy:writes=stack_2
        add w21, w13, #UNROLL                         // ...*................................................................................................
